//go:build ignore
// +build ignore

package examples

import (
	"github.com/knaka/go-pinfomap"
)

func main() {
	structInfo, err := pinfomap.GetStructInfo(".", "Bar", map[string]any{
		"AdditionalComment": "This is a comment.",
	})
	if err != nil {
		panic(err)
	}
	err = pinfomap.Generate(`
// Code generated by {{.GeneratorName}}; DO NOT EDIT.

package {{$.PackageName}}

import (
{{range $.Imports}}
	"{{.}}"
{{end}}
)

{{range $.Methods}}
func (recv {{if .PointerReceiver}}*{{end}}Baz) {{.Name}}() {
}
{{end}}{{/* range */}}
	`, structInfo)
	if err != nil {
		panic(err)
	}
}
